import React, {useEffect} from "react";
import { connect } from "react-redux";
import Category from "./Category";
import ListGroup from "react-bootstrap/ListGroup";
import Container from "react-bootstrap/Container";
import {getCategoriesFromApi} from "../../actions/categoryAction"

const Categories = (props) => {

    const categoryPics=[
        "https://www.foodbusinessnews.net/ext/resources/2020/9/Healthy-condiments_lead.png?1600101503",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT07RkhvWsy_SfMsFZVc-1jdAZH3yRk0AYu_EAU96bKR6ZJVFEyylKx5_omwRFFs7UrQ_U&usqp=CAU",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT5YGbDSAH0v5PNR5MAaWdKmSyDF4Z4B0KddA&usqp=CAU",
        "data:image/jpeg;base64,",
        "https://media.newyorker.com/photos/59095e38019dfc3494e9fa85/master/w_2560%2Cc_limit/Vara-Ancient-Grains-primary-2.jpg",
        "data:image/jpeg;base64,",
        "https://cdn-a.william-reed.com/var/wrbm_gb_food_pharma/storage/images/publications/food-beverage-nutrition/foodnavigator-usa.com/article/2019/03/27/merchandising-fresh-produce-shoppers-seek-more-snack-sized-and-local-options/9303073-1-eng-GB/Merchandising-fresh-produce-Shoppers-seek-more-snack-sized-and-local-options.jpg",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQPfv_m-Oru7x29ZQ4lJ9UGKzmsJ4nI96JNVw&usqp=CAU"
    ]
    const { getCategoriesFromApi } = props
    useEffect(() => {
      getCategoriesFromApi()
    }, [getCategoriesFromApi])
  return (
    <div>
      <Container>
        <ListGroup variant="flush">
          {props.categories.map((category, idx) => {
            return <Category key={idx} {...category} picture={categoryPics[idx]} />;
          })}
        </ListGroup>
      </Container>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    categories: state.categories,
  };
};

export default connect(mapStateToProps,{getCategoriesFromApi})(Categories);
